mapper.select:
SELECT *
FROM demand
WHERE demandType=#{demandType, jdbcType=VARCHAR}
order by demandId asc
limit #{index},#{length}

SELECT *
FROM demand
WHERE entId=#{entId, jdbcType=VARCHAR}
order by demandId asc
limit #{index},#{length}

mapper.delete:
delete
from demand
where demandId=#{demandId, jdbcType=INTEGER} and entId = #{entId,jdbcType = VARCHAR}

mapper.select:
select *
from demand
where demandType like #{condition} or demandTitle like #{demandTitle}
order by demandId asc
limit #{index},#{length}
