mapper.select:
SELECT id,type,title, date, content, digest
  FROM information where information.title like #{title,jdbcType=VARCHAR};
  order by date desc
  limit #{index},#{length};

 SELECT id,type,title, date, content, digest
FROM information
where date = #{date}
order by date desc
limit #{index},#{length};

 SELECT id,type,title, date, content, digest
FROM information
where type like #{type,jdbcType=VARCHAR}
order by date desc
limit #{index},#{length};

 SELECT id,type,title, date, content, digest
FROM information
where type like #{condition} or title like #{condition}
order by date desc
limit #{index},#{length};
