mapper.select:
SELECT *
FROM provide
WHERE proType=#{proType, jdbcType=VARCHAR}
order by proId asc
limit #{index},#{length}

SELECT *
FROM provide
WHERE proId=#{proId, jdbcType=VARCHAR}
order by proId asc
limit #{index},#{length}

SELECT *
FROM provide
WHERE proType like #{condition, jdbcType=VARCHAR} or proTitle like #{condition, jdbcType=VARCHAR}
order by proId asc
limit #{index},#{length}

mapper.delete:
delete
from pro
where proId=#{proId, jdbcType=INTEGER} and entId = #{entId,jdbcType = VARCHAR}
